mapper.select:
select a.id, a.name, u.name as owner, a.startDate, a.endDate
from tbl_activity a join tbl_user u on a.owner = u.id
and a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc limit #{count}, #{pageSize}

select count(*)
from tbl_activity as a join tbl_user as u on a.owner = u.id
and a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
-- > 表示大于该起始日期 and a.startDate > #{startDate}
-- < 表示小于结束日期 and a.endDate < #{endDate}
